#make the series of number into a comma seperated list
series=str()
seriesList = list(series)

n=13  #the amount of adjacdent digits
possible13Digits=[] #stores every 13 digit combination

for i in range(0,len(seriesList)-n+1):    #0 to that maximum possible index that gives a list of 13
    digits=seriesList[i:i+n]    #slices list from n to n+13 
    if '0' not in digits:  #checks if 0 is in the list(as multiplying by 0 gives 0)
        possible13Digits.append(''.join(digits)) #appends the list, in the form of combined string eg. '37..23','28..93',
print(possible13Digits)  #outputs the possible combinations

largestProduct=0  #initialising
largestSequence=0
for stringList in possible13Digits:  #for every string of 13 nums in the list
    product=1 # resets product to 1 every list
    listOf13 = list(stringList) #convert the list(a string) into a list of comma seperated digits eg. '3','2'....
    for digitChar in listOf13: 
        product*=int(digitChar)    #calcs product of every digit in the list of 13 digits
    if product>largestProduct:    #finds the largest product out of every combination
        largestProduct=product
        largestSequence=listOf13

print(largestSequence)
print(largestProduct)

    

        